mapper.select:
select no, title, contents, date_format(reg_date, '%Y/%m/%d') as regDate, category_no as categoryNo from post
where category_no = #{categoryNo}
where category_no = (select no from category where blog_id = #{blogId} order by reg_date asc limit 1)
order by reg_date desc;

select title, contents from post
where no = #{postNo};
where no = (select no from post where category_no = (select no from category where blog_id = #{blogId} order by reg_date asc limit 1) order by reg_date desc limit 1)
